INSERT INTO "RoomAmenity" (room_id, amenity_id)
SELECT r.id, a.id
FROM "Room" r
CROSS JOIN "Amenity" a
WHERE 
    -- Базовые удобства для всех номеров
    (a.name IN ('Wi-Fi', 'Кондиционер', 'Телевизор'))
    OR
    -- Дополнительные удобства для люксов
    (a.name IN ('Мини-бар', 'Сейф') AND r.name LIKE '%люкс%')
    OR
    -- Специальные удобства для определенных типов номеров
    (a.name = 'Мини-бар' AND r.name LIKE '%бизнес%')
    OR
    (a.name = 'Сейф' AND r.name LIKE '%президент%')
    OR
    (a.name = 'Сейф' AND r.name LIKE '%спа%')
    OR
    (a.name = 'Сейф' AND r.name LIKE '%кулинарный%')
    OR
    (a.name = 'Сейф' AND r.name LIKE '%антикварный%')
    OR
    (a.name = 'Сейф' AND r.name LIKE '%эксклюзивный%');
